class User
{
    [FaunaField("user_name")]
    public string UserName { get; set; }

    [FaunaIgnore]
    public string Password { get; set; }
}

var user = new User { UserName = "john", Password = "s3cr3t" };

var encoded = Encoder.Encode(user);

//encoded will be equivalent to:

ObjectV.With("user_name", "john")

class Product
{
    public string Description { get; set; }
    public double Price { get; set; }

    [FaunaConstructor]
    public Product(string description, double price)
    {
        Description = description;
        Price = price;
    }
}

class Order
{
    public string Number { get; set; }
    public List<Product> Products { get; set; }

    [FaunaConstructor]
    public static Order CreateOrder(string number, List<Product> products)
    {
        return new Order
        {
            Number = number,
            Products = products
        }
    }
}

